import Icon from '@chakra-ui/icon';
import { Box, HStack , Text, Button, Stack,} from '@chakra-ui/react';
import React from 'react';
import {BiErrorCircle} from 'react-icons/bi'
import {AiOutlineCheck} from  'react-icons/ai'
import { Stepper, StepConnector, Step, StepLabel,Tabs,Tab } from "@material-ui/core";

function Content(props) {

    const CustomTab = withStyles({
        root: {
          font: '500 20px "Montserrat", Sans-serif,all-petite-caps'
        },
        selected: {
          backgroundColor: '#00fff8',
          font: '500 18px "Montserrat", Sans-serif,all-petite-caps'
        },
      })(Tab);

      function TabPanel(props){

        const { children, value, index, ...other } = props;

        return(
          <>
           {value === index && (
            <>{children}</>
          )}
          </>
        )
      }

    return (
        <div class = "container-fluid" >
           <Box  backgroundColor="#f7f7f7"
              // height={{
              //   base: "1200px", // 0-48em
              //   md: "649px", // 48em-80em,
              //   xl: "700px", // 80em+
              // }}
              padding="20px"
              width={{ base: "100%", md: "100%", xl: "95%" }}
              borderWidth="2px"px
              marginStart = "20px"
              borderRadius="lg"
              shadow="2xl">
                  <HStack>
                        <Icon fontSize = "28px" as = {BiErrorCircle} />
                        <Text fontSize = "24px">Issues</Text>
                        <Box backgroundColor = "#E4EBF4" borderRadius = "30px" padding = "4px">
                        <Text  fontSize = "16px">{props.issuesL}</Text>
                        </Box>
                  </HStack>

                 <Box   backgroundColor="#E4EBF4"
                        borderRadius="20px"
                        shadow="xl"
                        padding={5}
                        marginTop = "20px">
                         <Tabs  value = {value} onChange = {handleTabs} variant = "fullWidth"  indicatorColor="white" 
          textColor="black" >
                          <CustomTab label = "Tenjin Managed Accounts"/>
                          <CustomTab  label = "Linked Accounts"/>
                        </Tabs>
                        <TabPanel value={value} index={0}>

                        </TabPanel>
                 </Box>

           </Box>
        </div>
    );
}

export default Content;
